mapper.insert:
INSERT INTO user_auth(open_id, user_name, session_key)
VALUES (#{openId}, #{userName}, #{sessionKey})
ON DUPLICATE KEY UPDATE session_key = #{sessionKey}

mapper.select:
SELECT session_key
FROM user_auth
WHERE id = #{id}

mapper.update:
UPDATE user_auth
SET session_key=#{sessionKey}
WHERE open_id = #{openId}

mapper.select:
SELECT open_id
FROM user_auth
WHERE id = #{id}

SELECT user_name
FROM user_auth
WHERE open_id = #{openId}
